void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
vec2 uv = fragCoord.xy / iResolution.xy;
vec3 center = vec3(0.5, 0.5, 0.5);
float size = 0.4;
vec3 p = vec3(uv - 0.5, 0.0) * 2.0;
float time = iTime * 0.5;
mat3 rotationX = mat3(1.0, 0.0, 0.0, 0.0, cos(time), -sin(time), 0.0, sin(time), cos(time));
mat3 rotationY = mat3(cos(time), 0.0, sin(time), 0.0, 1.0, 0.0, -sin(time), 0.0, cos(time));
p = rotationX * rotationY * p;
float inside = step(-size, p.x) * step(-size, p.y) * step(-size, p.z) *
step(p.x, size) * step(p.y, size) * step(p.z, size);
fragColor = vec4(inside * vec3(1.0, 0.0, 0.0), 1.0);
}
