void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 size = vec2(20.0, 20.0); 

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 sqr = fract(uv * (iResolution.xy / size)) * size;

    float hor = length(uv.x+uv.y) / 1.5 ;
    vec3 bgColor = vec3(0.0, 1.5-hor,  hor);

    int row = 5; 
    int col = 5; 
    if (sqr.x < float(col) && sqr.y < float(row)) {
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    } else {
        fragColor = vec4(bgColor, 1.0);
}
}
