void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Angle of the stripes (in radians)
    float angle = radians(45.0); // Adjust the angle as needed

    // Speed of the movement
    float speed = 0.2; // Adjust the speed as needed

    // Frequency of the stripes
    float frequency = 10.0; // Adjust the frequency as needed

    // Position along the diagonal line
    float diagonalPos = uv.x + uv.y * tan(angle);

    // Shifted position based on time to create movement
    float shiftedPos = diagonalPos + iTime * speed;

    // Stripe pattern using shifted position and frequency
    float stripePattern = step(mod(shiftedPos * frequency, 7.0), 2.5);

    // Green background color
    vec3 backgroundColor = vec3(0.0, 1.0, 0.0); // Green

    // Black stripe color
    vec3 stripeColor = vec3(0.0, 0.0, 0.0); // Black

    vec3 finalColor = mix(backgroundColor, stripeColor, stripePattern);

    // Output to screen
    fragColor = vec4(finalColor, 1.0);
}
