void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uvCols = fract(uv * 2.0);
    float columns = (step(uvCols.x,0.5));

    uv.y +=columns * iTime/10.0;
    uv.y -= (1.0-columns) * iTime/10.0;

    vec2 uvcCircles = fract(uv * 4.0);

    fragColor = vec4(uvcCircles, 0.50,1.0);
}
