void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying parameter
    float time = iTime * 0.5;

    // Calculate wave pattern
    float wave = sin(uv.x * 10.0 + time) * cos(uv.y * 10.0 + time) * 0.5;

    // Color based on the wave pattern
    vec3 color = vec3(0.5 + 0.5 * sin(time), 0.5 + 0.5 * sin(time + 2.0), 0.5 + 0.5 * sin(time + 4.0));
    color *= 0.2 + 0.8 * wave; // Darken the color based on the wave pattern

    // Output to screen
    fragColor = vec4(color, 1.0);
}
