float period = 3.0;

vec4 red = vec4(1.0, 0.0, 0.0, 0.0);
vec4 green = vec4(0.0, 1.0, 0.0, 0.0);
vec4 blue = vec4(0.0, 0.0, 1.0, 0.0);
vec4 white = vec4(1.0, 1.0, 1.0, 0.0);
vec4 yellow = vec4(1.0, 1.0, 0.0, 0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0;


    float fade = max(sin(iTime), 0.0);

    vec4 color;

    if(uv.x < 1.0)
    {
        if(uv.y < 1.0)
        {
            color = mix(texture(iChannel2, uv/4.0), blue, fade);
        }
        else
        {
            color = mix(texture(iChannel0, uv), red, fade);

        }
    }
    else
    {
        if(uv.y < 1.0)
        {
            color = mix(texture(iChannel3, uv), yellow, fade);
        }
        else
        {
            color = mix(texture(iChannel1, uv, 4.0), green, fade);

        }
    }



    fragColor = color;
}
