void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = 1200.0 / 675.0;
    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5;
    uv.x *= ratio;

    // float square = float(length(uv.x) < 0.3) * float(length(uv.y) < 0.3);
    float square = 1.0 - (smoothstep(0.34, 0.35, length(uv.x)) + smoothstep(0.34, 0.35, length(uv.y)));
    fragColor = vec4(square);
}
