vec3 palette( in float t)
{
    vec3 a = vec3(0.588,0.668,0.718);
    vec3 b = vec3(0.268,0.358,0.228);
    vec3 c = vec3(1.,1.,1.);
    vec3 d = vec3(4.285,-0.016,1.927);

    return a + b*cos( 6.28318*(c*t+d) );
}
vec3 getCol(vec2 uv,float i){

    float val = 
        sin(uv.x + cos(iTime * 0.7) * 5.) - 
        (sin(uv.y + sin(iTime* 0.7) * 5.) + uv.x * uv.y * uv.x * uv.y * 0.007);

    vec3 col = palette((val / 4.) + iTime * .5 + (i *.05));

    val = fract(val);

    //i think i ordered this step function wrong.
    val = step(val, 0.7  + (i *.2) - length(uv / (vec2(iResolution.x / iResolution.y, 1.) * 35.)));

    return col * val;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord * 2. / iResolution.xy - 1.;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 20. + iTime * 0.8;

    vec3 col = getCol(uv,0.);

    for(int i = 1; i <= 8; i++)
    {
        if(col != vec3(0.,0.,0.) ){
            break;
        }
        col = getCol(uv + normalize(uv)  * -0.1 * float(i), float(i)) * pow(0.75,float(i));
    }

    fragColor = vec4(col,1.0);
}
