// Fork of "Droste Zoom" by roywig. https://shadertoy.com/view/Xs3SWj  https://www.shadertoy.com/view/Ml33R7
// cf article http://www.josleys.com/article_show.php?id=82
// DesmosGraph: https://www.desmos.com/calculator/hbvzf9yk1r

void mainImage( out vec4 O, vec2 z )
{
    vec2  R = iResolution.xy;
          z = ( z+z - R ) / R.y; 
    float s = 2.3,   // log(10)
          a = .366,   // s/2PI
          t = iTime;

    z = mat2(1,-a,a,1) * vec2( log(length(z)), atan(z.y,z.x) ); // Conformal spiral mapping
    z = exp( mod(z.x-t, s) ) * cos(z.y +vec2(0,11)) ;           // Droste transform
    O = iMouse.z <= 0. 
         ? z = sin(4.*z), vec4( abs(z)/fwidth(z),1,1 ) // draw grid
         : texture(iChannel0,.5+.5*z*.1);              // drawtexture
}
