float drawFadeCircle(vec2 uv, float off) 
{
    float r = mod((iTime / 5.0) + off, 1.0);
    float intensity = 1.0 - r;
    float shape = 1.0 - step(r, length(uv));
    return intensity * shape;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv =  uv * 2.0 - 1.0;
    uv.x = uv.x * (iResolution.x / iResolution.y);

    const vec4 col = vec4(0.0, 0.1, 1.0, 1.0);
    const int iterations = 3;

    fragColor = vec4(0.0);
    for(int idx = 0; idx < iterations; ++idx) 
    {
        float off = float(idx) / float(iterations);
        fragColor += col * drawFadeCircle(uv, off);
    }
}
