void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;
    vec2 polarUV = vec2(atan(uv.x,uv.y),length(uv));

    vec3 tex = texture(iChannel0,polarUV - sin(iTime) * 0.5).rgb;

    vec3 maskValue = vec3(1.0f,1.0f,1.0f) - (tex * (3.0f + abs(sin(iTime)) * 0.3f));

    fragColor = vec4(maskValue,1.0f);
}
