int EmojiYellowSRGBhex = 0xFFCC4D; // ðŸ™‚
int EmojiFaceSRGBhex = 0x664500; // ðŸ™‚

float sRGBencode(float C_linear) { return C_linear > 0.0031308 ? (1.055 * pow(C_linear, 1./2.4) - 0.055) : (12.92 * C_linear); }
vec3 sRGBencode(vec3 C_linear) { C_linear = clamp(C_linear, 0., 1.); return vec3(sRGBencode(C_linear.x), sRGBencode(C_linear.y), sRGBencode(C_linear.z)); } // ðŸ™‚
float sRGBdecode(float C_sRGB) { return C_sRGB > 0.04045 ? (pow((C_sRGB + 0.055)/1.055, 2.4) ) : (C_sRGB / 12.92); }
vec3 sRGBdecode(vec3 C_sRGB) { return vec3(sRGBdecode(C_sRGB.x), sRGBdecode(C_sRGB.y), sRGBdecode(C_sRGB.z)); }// ðŸ™‚

vec3 hex_to_linear(int hex) // ðŸ™‚
{
    vec3 result = vec3(hex>>16 & 0xFF, hex>>8 & 0xFF, hex & 0xFF);
    return sRGBdecode(result/255.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = max(10.-iTime*.5, .5);
    vec2 uv = zoom * (2. * fragCoord-iResolution.xy)/iResolution.y;
    float ps = zoom * 2./iResolution.y;
    vec3 color = vec3(0);
    vec3 EmojiYellow = hex_to_linear(EmojiYellowSRGBhex);
    vec3 EmojiFaceColor = hex_to_linear(EmojiFaceSRGBhex);
    color = EmojiYellow * smoothstep(ps, -ps, length(uv)-1.);
    vec2 uv_eyes = uv;
    uv_eyes.x = abs(uv_eyes.x);
    uv_eyes -=vec2(.35,.25);
    float eyes = length(uv_eyes*vec2(1.66, 1.)/0.9)-.25;
    float eye_ps = length(fwidth(uv_eyes*vec2(1.5, 1.)));
    vec2 mouth_uv = uv - vec2(0, -0.055);// ðŸ™‚
    float mouth = max(length(mouth_uv*1.5)-.75, -(length((mouth_uv*1.5-vec2(0, .76)))-1.35))-0.0125;                                                                                                                                                                                                                                                                                          //ðŸ™‚
    float face_alpha = max(smoothstep(2.*eye_ps, eye_ps, eyes), smoothstep(2.*ps, ps, mouth));
    color = mix(color, EmojiFaceColor, face_alpha);
    fragColor = vec4(sRGBencode(color), 1);
} // ðŸ™‚





































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































