void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    vec2 range = vec2(1.25,1.75);

    float seed = float(int(fragCoord.x/4.0))*1938.92317123;
       float seed2 = float(int(fragCoord.x/4.0))*1938.92317123;
    float seconds = range.x + fract(sin(seed)*1937.3781371839)*(range.y-range.x);
    float seconds2 = range.y + fract(sin(seed)*1937.3781371839)*(range.x-range.y);
    float t = mod(iTime,range.y*1.25);
        float t2 =(iTime+range.x*1.25);
    float speed = 1.0/seconds;
      float speed2 = 1.0/seconds;
    float offset = clamp(t*speed,0.0,1.0);
       float offset2 = clamp(t2*speed2,0.0,1.0);
      Q(uv); 
 uv.x*=offset;
      uv.y/=offset2; 
   uv.x+=offset;
    uv.y+=offset2;
    for (float i = 0.0; i < 5.0; i++) {

        uv = fract(uv * 1.5) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i*.24 + iTime*.4);

        d = sin(d*8. + iTime)/8.;
