#define depth 256.0
#define off vec2(-0.5,0.0)
#define zoom 0.5
#define complex_mult(a,b) vec2(a.x*b.x-a.y*b.y,a.x*b.y+a.y*b.x)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5 * iResolution.xy ) /iResolution.y / zoom  + off;

    vec2 z = uv;

    float value = 0.0;

    for(float i = 0.0; i<depth; i+=1.0) {
        z = complex_mult(z,z) + uv;

        if(dot(z,z) > 4.0) {
            value = i / depth;
        }
    }

    fragColor = vec4(value,0.0,0.0,1.0);
}
