float power =3.;
float pi= 3.14159;
vec2 cPow(vec2 z,float p){
    float t=p*(atan(z.y/z.x)+pi*float(bool(z.x<0.)));
    float R= length(z);
    return pow(R,p)*vec2(cos(t),sin(t));
}
vec2 cDiv(vec2 z1,vec2 z2){
    return vec2((z1.x*z2.x)+(z1.y*z2.y),(z2.x*z1.y)-(z1.x*z2.y))/(pow(z2.x,2.)+pow(z2.y,2.));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ITERATIONS = 300.;
    float ZOOM;
    vec2 COORDS;
    if (iMouse.z>0.0){
    ZOOM =25.;
    COORDS=4.*((iMouse.xy)-(iResolution.xy/2.))/(iResolution.x);
   }else{
    ZOOM =1.;
    COORDS = vec2(0.0,0.0);
   }
    COORDS=COORDS-(COORDS/ZOOM);

    vec2 uv = COORDS+((4.0/ZOOM)*(fragCoord/iResolution.x))-iResolution.xy/(ZOOM*.5*iResolution.x);

    vec2 z =vec2(1,0);
    vec2 z0;
    float i=0.;

    for (;i<ITERATIONS && distance(z0,z)>0.001;++i){
    z0 =z;
    z = uv+z-cDiv(cPow(z,power)-vec2(1.,0.),power*cPow(z,power-1.));
    }
    vec3 col =vec3(0.);
    float time = (iTime*pi/10.)+3.85;
    if(i!=ITERATIONS){
        i=i/16.;
        col = abs(vec3(sin(i+time),sin(i+(time/3.0)),sin(i+(time/2.0))));}
    fragColor = vec4(col,1.0);
}
