void mainImage( out vec4 o, in vec2 f )
{
    vec4 white = vec4(1,1,1,0);
    vec4 black = vec4(0);
    vec2 uv = f/iResolution.xy - vec2(0,0.5);    
    float w = 1./(30.+400.*uv.x);
//    float w = 0.05/2.;
    o = black;
    if ( (abs(uv.y) > w) && (abs(uv.y) < 3.*w) ) o=white;
    if ( (abs(uv.y) > 5.*w) && (abs(uv.y) < 7.*w) ) o=white;
    if ( (abs(uv.y) > 9.*w) ) o=white;
}
