vec3 mainColor = vec3(66.0/255.0,135.0/255.0,245.0/255.0); 
vec3 secondaryColor = vec3(55.0/255.0,125.0/255.0,230.0/255.0); 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 fragment = fragCoord.xy/iResolution.y;
    vec2 uv = fragment;
    fragment += iTime*0.1;
    fragment *= 9.0;

    float sinT = sin(iTime*0.5);
    float k = 10.0;
    float t = sign(sinT) * (1.0 - exp(-k*abs(sinT)));
    fragment.x += 0.5 * t * step(1.0, mod(fragment.y, 2.0));

    vec2 whole = floor(fragment);
    vec2 fraction = fract(fragment);

    fragColor = vec4(mix(mainColor, secondaryColor,step(fraction.x + 0.5*fraction.y, 1.0) *
        step(0.0, fraction.x + -0.5*fraction.y)), 1.0);
}
