void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy/iResolution.xy; // compute pixel
    uv -= 0.5; // center it
    uv.x *= iResolution.x / iResolution.y; // normalize center
    float len = length(uv); // compute length

    float radius = 0.2 * mod(iTime, 0.5);
    float col = smoothstep(radius, radius - 0.01, len);

    fragColor = vec4(col, col, col, 1.0);
}
