vec3 hsv( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	rgb = rgb*rgb*(3.0-2.0*rgb);
	return c.z * mix( vec3(1.0), rgb, c.y);}
//smooth hsv from iq https://www.shadertoy.com/view/MsS3Wc

#define go void mainImage( out vec4 O, in vec2 I )
#define R iResolution
#define C0 iChannel0
#define C1 iChannel1
#define cut(x) clamp(x,-1.,1.)
#define tex(x,y) texelFetch(x,y,0)
#define N 9.

go{
float a=0.;
float me = tex(C0,ivec2(I)).x;
for(float i=-N;i<=N;i++){
for(float j=-N;j<=N;j++){
float d=sqrt(i*i+j*j);
if(d>N){continue;}
a+=.04*tex(C0,ivec2(mod(I.x+i,R.x),mod(I.y+j,R.y))).x*cos(d-me*sin(d*.2));
}}
O=vec4(cut(a));
if(iFrame<1 && distance(R.xy*.5,I)<1.){O=vec4(1.);}
if(iMouse.z>0. && distance(I,iMouse.xy)<25.){O=vec4(1.);}
}

go{
float a=0.;
float me = tex(C0,ivec2(I)).x;
for(float i=-N;i<=N;i++){
for(float j=-N;j<=N;j++){
float d=sqrt(i*i+j*j);
if(d>N){continue;}
a+=.04*tex(C0,ivec2(mod(I.x+i,R.x),mod(I.y+j,R.y))).x*cos(d-me*sin(d*.2));
}}
O=vec4(me-cut(a));
}

