void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 4. * fragCoord / iResolution.xy - 2.;
    uv.x *= iResolution.x / iResolution.y;

    vec4 z = vec4(uv * 0.5, 0.7 * sin(iTime * 2.1761), 0.6 * cos(iTime * 1.763));
    for (float i = 0.; i < 1000.; ++i)
    {
        z.xy = vec2(z.x * z.x - z.y * z.y, 2. * z.x * z.y) + z.zw;
        if (dot(z.xy, z.xy) > 1e30)
        {
            fragColor = .5 * vec4(sin(i * .2), cos(i * .2), sin(i * 0.3), 1.) + .5;
            return;
        }
    }
    fragColor = vec4(0);
}
