void mainImage(out vec4 o, vec2 u){
    float d, s, 
          i = 12., 
          t = .1 * iTime;

    vec3 R = iResolution, q, D, 
         m = .3 * vec3(cos(t), 5. * sin(t), 0),
         p = m + vec3(0, 0, - 1);
         u = (u + u - R.xy) / R.y;

    D = vec3(u, 2) / 1e3;

    while(i++ < 90.) {
        int j; 

        p += D;
        q = p;

        s = (.0005 * length(q) - .0004) * q.x;

        while(j++ < 12) 
            q /= dot(q, q),
            q = abs(q) - .6;

        d -= length(q) * s;
    }

    d *= 90.;
	o = vec4(3.*d*d*d, d*d, d*.5, 0);
}
