#ifdef GL_ES
precision mediump float;
#endif

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    float rainDropDensity = 50.0;
    float baseRainSpeed = -5.5;
    float baseDropSize = 0.00001;
    float brightness = 0.9;
    float alpha = 0.0;

    for (float i = 0.0; i < rainDropDensity; i++) {
        vec2 dropPos = vec2(
            fract(sin(i * 12.9898) * 43758.5453),
            fract(sin(i * 78.233) * 43758.5453 + iTime * baseRainSpeed * (0.5 + fract(sin(i * 34.789) * 12345.678) * 0.5))
        );
        float distance = length(uv - dropPos);
        alpha += exp(-distance * distance / (baseDropSize * (0.5 + fract(sin(i * 56.789) * 98765.432) * 0.5)));
    }

    fragColor = vec4(vec3(brightness) * alpha, alpha);
}
