// 2024-8-30
// Very strange shape.

// jt's dodecagon triangle square tiling
// https://www.shadertoy.com/view/lcBfWR
//
// hexagon golf (352) 244 chars
// https://www.shadertoy.com/view/lcjBWz
//
// Pentagonal radial tiling
// https://www.shadertoy.com/view/lcSfW1





// FabriceNeyret2's very novel approach, thanks
#define P outerProduct

void mainImage(out vec4 o, vec2 U) 
{
    vec2 p = iResolution.xy, D,
         k = vec2(-.5, .866),
         m = vec2(-.259, .966),
         t = vec2(1.866, 1);
    mat2 M = mat2( abs( U+U - p ).xyyx ) *8./p.y;

    M = mat2(abs(vec4( M - P( max(M*k,0.), k+k) )));   
    M -=                   P( max(M*m,0.), m+m) -  P( step(0.,M*m), abs(k.yx) ); 

    D = abs(   min( mod( M[0]                    , t.x ) -k.y, 0.) * .58
             + abs( mod( M[1]-.5* floor(M[0]/t.x), t.y ) +k.x )
           );  
    o = vec4( .8 - .01 / min(D[0],D[1]) );
}



/*
// If Windows doesn't see the "cube", try this
vec2    p,
        k = vec2(-.5, .866),
        m = vec2(-.259, .966),
        t = vec2(1.866, 1);

vec4 D(vec2 q){ 
        p = abs(dot(q, k)>0. ?reflect(q,k):q); 
        p = dot(p,m)>0. ? p.x++, reflect(p, m):p, 
        p.y -= .5 * floor(p/t).x, 
        p = mod(p,t)-abs(k).yx;
        return abs(abs(p.yyyy) + min(p.x, 0.)*.58);
}

void mainImage(out vec4 o , vec2 u){
        p = iResolution.xy,
        u = 6. * abs(u+u-p)/p.y;
        o = .8 - .01/min(D(u), D(u.yx));
}



