vec3 palette( in float t ) {
    vec3 a = vec3(0.530, 0.530, 0.530);
    vec3 b = vec3(-0.507, -0.507, -0.507);
    vec3 c = vec3(0.991, 0.991, 0.968);
    vec3 d = vec3(1.447, 1.497, 1.557);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

vec2 uv0 = uv;

vec3 fcol = vec3(0.0);

for (float i = 0.0; i < 4.0; i++) {

uv *= 2.0;
uv = fract(uv);
uv -= 0.5;

float d = length(uv);

d = sin(d*4. + iTime*2.);
d = abs(d);

d = 0.7/d;

vec3 col = ((palette(length(uv0)*uv.x + uv.y)/abs(uv.y + uv.x))/900.);
col *= d;

fcol += col*d;

}

fragColor = vec4(fcol, 1.0);

}
