// Fork of "better template" by ProfessorPixels. https://shadertoy.com/view/l3yGRV
// 2024-07-25 22:38:12

void mainImage(out vec4 O,vec2 F)
{
    F-=iResolution.xy*.5;
    F=vec2(atan(F.x,F.y),length(F));
    F.x*=1.+2.*iMouse.x/iResolution.x;
    F=F.y*sin(F.x+vec2(0,1.5708));
    F*=.1;
    O=vec4(sign(cos(F.x)*cos(F.y)));
}
