const float angle = 45.0;
const float shift = 0.0125;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 tex = texture(iChannel0, uv);
    tex.r = texture(iChannel0, vec2(uv.x - (shift * cos(angle)), uv.y - (shift * sin(angle)))).r;
    tex.b = texture(iChannel0, vec2(uv.x + (shift * cos(angle)), uv.y + (shift * sin(angle)))).b;
    fragColor = tex;
}
