float randomRange(float min, float max, float seed) {
    float randValue = fract(sin(seed) * 43758.5453);
    return min + randValue * (max - min) * cos(iTime/10.0);
}

float circle(float rad, vec2 pos, vec2 uv, float blurring){
    float x = (uv.x-pos.x)*(uv.x-pos.x) * randomRange(1.0-blurring, 1.0+blurring, uv.x + iTime);
    float y = (uv.y-pos.y)*(uv.y-pos.y) * randomRange(1.0-blurring, 1.0+blurring, uv.y + iTime);;

    if(x + y <= rad*rad){
    return 1.0 + 3.0*sin((iTime + uv.x*cos(iTime/17.0) + uv.y*cos(iTime/19.0))*35.0*sin(iTime/21.0));
    }
    return 1.0 * cos(iTime + uv.x);//uv.x*uv.x + uv.y*uv.y - rad*rad;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    float randValueX = randomRange(0.0, 1.0, uv.x + iTime);
    float randValueY = randomRange(0.0, 1.0, uv.y + iTime);

    vec3 col = cos(iTime + uv.xyy) + vec3(randValueX, randValueY, randValueX);
    col = col * vec3(sin((iTime+uv.y)/tan(uv.x*3.1415)), cos(iTime/sin(uv.x)), cos(sin(uv.x)*uv.x) );

    col = col * circle(0.1,vec2(0.2, 0.7), uv, 0.5);
    col = col * circle(0.15,vec2(0.3, 0.6*cos(iTime/11.0)), uv, 0.4);
    col = col * circle(0.25,vec2(0.4*cos(iTime/8.0), 0.5), uv, 0.3);
    col = col * circle(0.3,vec2(0.5, 0.3), uv, 0.5);



    // Вывод на экран
    fragColor = vec4(col, 1.0);
}
