#define RND(X) fract(sin(dot(X, vec2(131.0, 1234.1))) * 2132.1)
#define RT(X) mat2(cos(X), -sin(X), sin(X), cos(X))

float s_noise(vec2 uv)
{
	vec2 i = floor(uv);
	vec2 f = fract(uv);
	float a = RND(i + vec2(0, 0));
	float b = RND(i + vec2(1, 0));
	float c = RND(i + vec2(0, 1));
	float d = RND(i + vec2(1, 1));

	vec2 u = smoothstep(0.0, 1.0, f);
	return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;


}

void mainImage(out vec4 c_out, in vec2 uu)
{
	vec2 rr = iResolution.xy;
    vec2 uv = (uu + uu - rr) / rr.y;
	float t = mod(iTime, acos(-1.0) * 5.0);

	uv.x += sin(uv.y * 4.0) * 0.5;
	uv *= 2.0 - clamp(t - 1.0, 0.0, 1.0) * 1.6;

	uv *= RT(uv.x + t);	

	for (int i = 0; i < 7; i++)
	{
		uv = abs(uv) - vec2(0.1 * sin(uv.y), 0.2 * cos(uv.x));
		uv *= RT(t);
		uv *= 1.1;
	}

	for (int i = 0; i < 5; i++) {

		uv = abs(uv) - vec2(0.35, 0.20);
		uv *= RT(uv.x + 0.31 + s_noise(uv + t));

	}


	vec3 clr = vec3(0);
	float fr = sin(uv.x) * 0.75;
	clr.r += (smoothstep(uv.y - 0.01, uv.y, fr) - smoothstep(uv.y, uv.y + 0.05, fr)) * s_noise(uv * 5.0 + t);
	float fg = sin(uv.x) * 0.85;
	clr.g += (smoothstep(uv.y - 0.01, uv.y, fg) - smoothstep(uv.y, uv.y + 0.05, fg)) * s_noise(uv * 5.0 + t);
	float fb = sin(uv.x) * 0.95;
	clr.b += (smoothstep(uv.y - 0.01, uv.y, fb) - smoothstep(uv.y, uv.y + 0.05, fb)) * s_noise(uv * 5.0 + t);


	c_out = vec4(pow(clr, vec3(0.4545)), 1.0);
}
