void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;


    float radius = length(uv);
    float angle = atan(uv.y, uv.x);


    float twist = sin(angle * 8.0 + radius * 20.0 - iTime * 5.0) * 0.5;
    float rift = abs(twist - 0.2 * cos(radius * 10.0 - iTime));


    float glow = exp(-rift * 15.0) * 1.5;
    float edge = smoothstep(0.1, 0.08, rift);


    vec3 baseColor = vec3(0.2, 0.1, 0.5);
    vec3 riftColor = vec3(0.8, 0.2, 0.3) + 0.5 * sin(iTime + vec3(0.0, 2.0, 4.0));
    vec3 color = mix(baseColor, riftColor, edge);


    color += glow * vec3(1.0, 0.8, 0.5);


    float noise = fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
    color += 0.05 * noise * vec3(1.0, 0.8, 0.6);


    float vignette = 1.0 - smoothstep(0.8, 1.0, radius);
    color *= vignette;


    fragColor = vec4(color, 1.0);
}
