void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float t = iTime * 2.0;

    float a = atan(uv.y, uv.x); 
    float r = length(uv); 

    float tunnel = sin(10.0 * r - t) * 0.5 + 0.5;

    float rings = mod(a * 6.0 + t, 6.2831) / 6.2831; 
    rings = abs(rings - 0.5) * 2.0; 

    float colorFactor = smoothstep(0.2, 0.3, rings) * tunnel;

    float glow = 0.02 / (r + 0.02);

    vec3 color = vec3(0.0, 0.5, 1.0) * colorFactor + vec3(1.0, 0.5, 0.0) * glow;

    fragColor = vec4(color, 1.0);
}
