// ShaderToy simulation of dynamic electric, magnetic, and gravitational fields with advanced field interactions.
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Coordinate setup
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // Time-dependent animation
    float time = iTime * 2.0;

    // Central mass position
    vec2 center = vec2(0.0, 0.0);

    // Distance from center
    float dist = length(uv - center);

    // Electric field: Radial lines from the center
    float electricField = sin(10.0 * atan(uv.y - center.y, uv.x - center.x) + time) / dist;

    // Magnetic field: Circular loops around the center
    float magneticField = sin(20.0 * dist - time);

    // Standing wave: Oscillations in place
    float standingWave = sin(15.0 * dist) * cos(time);

    // Traveling wave: Propagating outward
    float travelingWave = sin(15.0 * (dist - time));

    // Gravitational wave: Periodic ripples modulating the field
    float gravitationalWave = sin(8.0 * dist - time) / (1.0 + dist * dist);

    // Interaction effects: Highlighting field overlaps and interference
    float interaction = sin(standingWave * travelingWave) + cos(gravitationalWave * electricField);

    // Combine fields, waves, and interactions with color representation
    vec3 color = vec3(
        0.5 + 0.5 * (electricField + interaction), // Electric field with interaction (red component)
        0.5 + 0.5 * (magneticField + interaction), // Magnetic field with interaction (green component)
        0.5 + 0.5 * (standingWave + travelingWave + gravitationalWave + interaction) // Combined waves with interaction (blue component)
    );

    // Output the color
    fragColor = vec4(color, 1.0);
}
