float zz(vec3 v1,vec3 v2,vec3 v3,float x,float y){
    float a=(v2.y-v1.y)*(v3.z-v1.z)-(v3.y-v1.y)*(v2.z-v1.z);
    float b=(v2.x-v1.x)*(v3.z-v1.z)-(v3.x-v1.x)*(v2.z-v1.z);
    float c=(v2.x-v1.x)*(v3.y-v1.y)-(v3.x-v1.x)*(v2.y-v1.y);
    return ((y-v1.y)*b-(x-v1.x)*a)/c+v1.z;
}

int pr(vec2 a,vec2 b,vec2 uv){
if((b.x-a.x)*(uv.y-a.y)-(b.y-a.y)*(uv.x-a.x)>=0.) return 1;
else return 0;
}

float sh(vec2 uv,vec4 c){
    float q;
    if((uv.x-c.x)*(uv.x-c.x)+(uv.y-c.y)*(uv.y-c.y)<c.w*c.w) q=c.z+sqrt(c.w*c.w-((uv.x-c.x)*(uv.x-c.x)+(uv.y-c.y)*(uv.y-c.y)));
    else q=-10.;
    return q;
}

float sq(vec2 uv,vec4 c){
    float q;
    if((uv.x>c.x)&&(uv.x<c.x+c.w)&&(uv.y>c.y)&&(uv.y<c.y+c.w)) q=c.z;
    else q=-10.;
    return q;
}

float tr(vec2 uv,vec3 v1,vec3 v2,vec3 v3){
    float q;
    q=zz(v1,v2,v3,uv.x,uv.y);
    int t=0;
    vec2 a,b,c;
    a.x=v1.x;a.y=v1.y;b.x=v2.x;b.y=v2.y;c.x=v3.x;c.y=v3.y;

    t=pr(a,b,uv)+pr(b,c,uv)+pr(c,a,uv);
    //if((q<v1.z&&q<v2.z&&q<v3.z)||(q>v1.z&&q>v2.z&&q>v3.z)) q=-10.;
    if(t!=0) q=-10.;
    return q;
}




