#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e/2.)*e;
    p.z-=4.;
    p.xz*=R2D(iTime*.5);

    for(j=0.;j++<8.;)
        p=abs(abs(p)-.02)-.01,
        d=min(atan(p.z,p.y),exp2(p.x))/6.5,
        d-=dot(cos(p/q),sin(p-q))/2e4;

    e-=d;

}o+=vec4(exp(p-q),1)*5./i;
}
