// On my Pixel 4a, this shows all-white - ie. the fract has returned 0.0
// Chrome 123.0.6312.118
// Android 13; Pixel 4a Build/TQ3A.230805.001.S1

float f(highp sampler2D chan) {
 return fract(texelFetch(chan,ivec2(0),0).x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#if 1
  // Applying fract directly to texelFetch output goes wrong
  float x = fract(texelFetch(iChannel0,ivec2(0,0),0).x);
#elif 1
  // This works - indirect via function with highp sampler2D param
  float x = f(iChannel0);
#else
  // Apply fract to variable and all is well
  float x = texelFetch(iChannel0,ivec2(0,0),0).x;
  x = fract(x);
#endif  
  fragColor = vec4(x == 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(2048.5);
}
