float zoom = 8.;

//Constants
float pi = 3.14159;

//https://stackoverflow.com/questions/4200224/random-noise-functions-for-glsl
float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

vec2 polar(vec2 pos){
    pos += 0.0001;
    return vec2(
        length(pos),
        atan(pos.y / pos.x) / (pi * 2.) + 
        (pos.x * pos.y < 0. ? 0.5 : 0.) +
        (pos.y < 0. ? 0.5 : 0.)
    );
}

vec3 palette(float t)
{
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.,1.,1.);
    vec3 d = vec3(0.333,0.666,1.);

    return a + b * cos(6.28318 * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord * 2. / iResolution.xy - 1.;
    pos.x *= iResolution.x / iResolution.y;
    pos *= zoom;

    vec2 cnt = round(pos);
    vec2 pol = polar(pos);    
    vec2 pol2 = polar(pos - cnt);

    float val = 1. - length(cnt - pos);
    val *= sin(pol.y * pi * 16. + sin(pol.x) * 8. / pol.x + -iTime * 3.) / 2. + 0.3;
    val = abs(0.2 / (0.5 - val));
    val *= val;

    vec3 col = palette(
        iTime +
        (mod(cnt.x + cnt.y, 2.) == 0. ? 
        pol2.y + (mod(cnt.x, 2.) == 0. ? 0. : 0.5) : 
        -pol2.y + (mod(cnt.x, 2.) == 0. ? 0. : 0.5) ) 
    );

    fragColor = vec4(col * val,1.);
}
