void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float a = length(cos(uv.x + cos(iTime) * 0.5 + 20.0)*0.5+0.5 * iResolution.y) * 30.0;
    float b = length(cos(uv.y + sin(iTime) * 0.5 + 20.0)*0.5+0.5 * iResolution.x) * 30.0;

    float colorR = ( cos(b)*cos(a)* cos(iTime) * 15.0);
    float colorG = ( cos(a)*sin(b)* sin(iTime) * 10.0);
    float colorB = ( sin(b)*cos(a)* cos(iTime) * 25.0);


    fragColor = vec4(colorR,colorG,colorB,1.0);
}
