void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 mv = (iMouse.xy * 2.0 - iResolution.xy) / iResolution.y;
    float t = iTime * 0.25 - 1.0;
    mv.y = mix(t, mv.y, step(1.0, t));
    uv *= 12.0;
    vec2 uv2 = uv;
    mv *= 12.0;
    mat2 m = mat2(1,0.25,0.125,1);
    uv *= inverse(m);
    vec2 mv2 = mv * inverse(m);
    uv -= 1.0;
    mv2 -= 1.0;
    vec2 c = uv;
    vec2 d = round(uv*0.5)*2.0;
    uv -= d;
    uv *= m;
    d *= m;
    mv2 *= m;
    float h1 = clamp((mv2.y - d.y) * 0.125 + 1.0, 0.0, 1.0);
    float h2 = clamp(1.0 - (mv2.y - d.y) * 4.0, 0.0, 1.0) * step(0.999, h1);
    float ph = h1 * 3.1415926535;
    mat2 r = mat2(-cos(ph),-sin(ph),sin(ph),-cos(ph));
    vec3 col = vec3(smoothstep(0.9,0.85,length(uv)));
    col *= smoothstep(0.75,0.85,length(uv));
    col += vec3(smoothstep(0.7,0.65,length(uv))) * vec3(1.0,vec2(smoothstep(-0.02,0.02,mix((uv*r).x,uv.x,smoothstep(0.5,0.55,length(uv))))));
    col *= h1;
    col *= smoothstep(mv2.y - 0.02, mv2.y, d.y);
    col += vec3(0,vec2(smoothstep(0.9, 0.85*h2, length(uv)) * sqrt(h2)));
    col *= smoothstep(-9.01,-8.99,c.x);
    col *= smoothstep(7.01,6.99,c.x);
    col *= smoothstep(3.01,2.99,c.y);
    col *= smoothstep(-5.01,-4.99,c.y);
    col = max(col, smoothstep(0.01, 0.0, (uv2.y - mv.y) * (uv2.y - mv.y)));

    fragColor = vec4(col,1.0);
}

vec2 mainSound( int samp, float time )
{
    float o = 0.0;
    for (int i = 0; i < 4; i++)
        for (int j = 0; j < 8; j++)
        {
            float s = float(i*8+j+32) / 12.0;
            float x = max(0.0, time - s);
            float f = 440.0 * pow(2.0, float(-i - j) / 12.0);
            float _e = log(x) * 0.4 + 2.0;
            float e = exp(-_e*_e) * 0.5;
            o += sin(x*f*6.2831853) * e;
        }
    o *= step(time, 8.0);
    return vec2(o);
}
