// USE MOUSE

#define animation(a) a*iTime
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

void mainImage( out vec4 color, in vec2 O )
{O=(O)/length(iResolution.xy)*40.0;

    float speed=30.0;

    vec2 O2 = O*rot(radians(60.0));
    vec2 O3 = O*rot(radians(120.0));
    float X = O.x, Y=O2.x, Z=O3.x;

    float a = abs(sin(iTime/3.14159)*5.9)+0.1;
    color = vec4( 
        float((int((pow((sin(X)),3.0)+pow((sin(Y)),3.0)+pow((sin(Z)),3.0))*255.0+animation(speed*4.0)))&255)/255.0
    );

    color.y = float((int((pow((sin(X)),2.0)+pow((sin(Y)),2.0)+pow((sin(Z)),2.0))*255.0+animation(speed*3.0)))&255)/255.0;
    color.z = float((int((pow((sin(X)),1.0)+pow((sin(Y)),1.0)+pow((sin(Z)),1.0))*255.0+animation(speed*5.0)))&255)/255.0;
}
