const float originalScale = 2.0;
float scale = originalScale;

vec2 cMul(in vec2 z1, in vec2 z2)
{
    return vec2(z1.x*z2.x-z1.y*z2.y,z1.x*z2.y+z2.x*z1.y);
}

const vec2 x0 = vec2(sqrt(2.)*0.5,sqrt(2.)*0.5);
const vec2 x1 = vec2(-sqrt(2.)*0.5,sqrt(2.)*0.5);
const vec2 x2 = vec2(-sqrt(2.)*0.5,-sqrt(2.)*0.5);
const vec2 x3 = vec2(sqrt(2.)*0.5,-sqrt(2.)*0.5);

vec2 cSqr(in vec2 z)
{
    return vec2(z.x*z.x-z.y*z.y,2.*z.x*z.y);
}

vec2 cConj(in vec2 z)
{
    return vec2(z.x,-z.y);
}

vec2 cInv(in vec2 z)
{
    return cConj(z)/(length(z)*length(z));
}

vec2 cDiv(in vec2 z1, in vec2 z2)
{
    return cMul(z1, cInv(z2));
}

vec2 func(in vec2 z)
{
    return cSqr(cSqr(z))+vec2(1.,0.);
}

vec2 funcPrime(in vec2 z)
{
    return 4.*cMul(cSqr(z),z);
}

vec2 camera = vec2(0.0,0.0);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int iters = int(floor(iTime));
    camera -= (iMouse.xy/iResolution.xy*vec2(originalScale)-vec2(originalScale/2.0))/vec2(iResolution.y/iResolution.x,1.0);
    vec2 normalizedPosition = (fragCoord/iResolution.xy*vec2(scale)-vec2(scale/2.0))/vec2(iResolution.y/iResolution.x,1.0) + camera;
    vec2 coord = normalizedPosition;
    for (int i = 1; i <= iters; i++)
    {
        coord = coord - cDiv(func(coord), funcPrime(coord));
    }
    float dist0 = distance(coord,x0);
    float dist1 = distance(coord,x1);
    float dist2 = distance(coord,x2);
    float dist3 = distance(coord,x3);
    float minDist = min(min(dist0, dist1), min(dist2, dist3));
    if (dist0 == minDist){
        fragColor = vec4(1,0,0,1);
    }
    else if (dist1 == minDist){
        fragColor = vec4(0,1,0,1);
    }
    else if (dist2 == minDist){
        fragColor = vec4(0,0,1,1);
    }
    else {
        fragColor = vec4(1,0,1,1);
    }
}
