float Xor(float a, float b){
    return a*(1.-b)+b*(1.-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    /*
    float a = .707;
    float s = sin(a);
    float c = cos(a);
    uv *= mat2(c, -s, s, c);
    */
    uv *= 10.;

    vec2 gv = fract(uv)-.5;
    //vec2 id = floor(uv);

    float m = 0.;
    float t = iTime;


    for(float y = -1.;y<=1.;y++){
        for(float x = -1.;x<=1.;x++){
            vec2 offs = vec2(x,y);
            float d = length(gv+offs);
            float dist = length(uv-offs)*.3;
            float r = mix(.3, 1.5, sin(dist-t)*.5+.5);
            m = Xor(m, smoothstep(r, r*.9, d));  
        }
    }
    col.rg = gv;
    col += m;

    fragColor = vec4(col,1.0);
}
