vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}
#define time iTime
#define resolution iResolution.xy
//https://www.shadertoy.com/view/mtyGWy


vec3 palette2( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(1.0, 0.5, 0.5);
    vec3 c = vec3(1.5, 0.5, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}
#define rotation(angle) mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

float TAU = 2.*3.14159;

// Thanks IQ
float eqTri( in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.x = p.x + r/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
 vec2 p = fragCoord/iResolution.xy-0.5;
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float a = .707*iTime;
    float s = sin(a);
    float c2 = cos(a);

  	float t3 = iTime * .1 + ((.25 + .05 * sin(iTime * .1))/(length(uv.xy) + .07)) * 2.2;
	float si = sin(t3);
	float co = cos(t3);
    mat2 ma = mat2(-co, si, si, co);

       vec2 uv2 = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
 vec3 r2 = normalize(vec3(uv, 1.1 - dot(uv, uv) *4.002*cos(iTime)));
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

     vec3 c=vec3(0);

  uv+=r2.xy;
    for(int i=0;i<40;i++){
        float t = 2.*3.14*float(i)/20. * time*0.1;

        float x = cos(t*1.5);
        float y = sin(t+cos(time / 1.0));

        vec2 o = .2*vec2(x,y);

        c += 0.01/(length(p-o))*vec3(0.2);
    }
             uv *= rotation(3. * TAU * (.3 - clamp(length(uv2),0.,.3)));
          uv*=vec2(fract(log(length(uv.xy))+iTime*0.25));
uv2+=r2.xy;
uv-=uv2;
    for (float i = 0.0; i < 4.0; i++) {

        uv = fract(uv * 1.5) - 0.5;

uv *= mat2(c2, -s, s, c2);
        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i*.4 + iTime*.4);

        d = sin(d*8. + iTime)/8.;
        d = abs(d);

        d = pow(0.01 / d, 1.2);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
