//NOTA: EL ORDEN VERTICAL ES SUPER IMPORTANTE

//paleta de colores: cosine based palette, 4 vec3 params
vec3 palette( float t ) {
    vec3 a = vec3(0.369,0.369,0.369);
    vec3 b = vec3(0.000,0.761,0.051);
    vec3 c = vec3(0.000,0.298,1.000);
    vec3 d = vec3(1.000,1.000,1.000);

    return a + b*cos( 6.28318*(c*t+d) );
}

//(fragColor: coordenadas ya pintadas, fragCoord: coordenadas a pintar)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //convertimos el valos normalizado de las coordenadas de pantalla -1 a 1
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y; 

    //vector que rastre pixel por pixel
    vec2 uv0 = uv;

    //color final
    vec3 finalColor = vec3(0.0);

    //agregamos iteraciones
    for(float i = 0.0; i < 6.0; i++){

        //agregamos repeticion espacial (division de pantalla) y ajsutamos las coordenadas
        uv = fract(uv * 3.5) - 0.5;

        //creamos una variable para ver la longitud del uv
        float d = length(uv) * -exp(-length(uv0));     

        //agregamos una variable de color
        vec3 col = palette(length(uv0) + i * 0.4 + iTime * .1);    

        //al agregar sin y un tiempo podemos crear una animacion de repetecion
        d = sin(d*8. + iTime)/8.;

        //obtenemos el valor absoluto de d
        d = abs(d);

        d = pow(0.012 / d, 1.2) * i;

        //agregamos el color a la longitud
        finalColor += col * d;   
    }

    //salida a la pantalla
    fragColor = vec4(finalColor, 1.0);
}
