void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord - iResolution.xy / 2.0) / iResolution.y;


    float angleSegments = floor(6.0 + mod(iTime * 0.2, 8.0)); 
    uv = vec2(
        length(uv) * (10.0 + 2.0 * sin(iTime * 0.2)), 
        sin(atan(uv.x, uv.y) * angleSegments + iTime * 0.3)
    );


    vec3 col = vec3(0);


    uv += sin(uv.yx * (sin(iTime * 0.4) * 2.0) + iTime * 0.5 * vec2(1.2, 1.0));


    col += 1.0 - smoothstep(0.1, 0.2, length(uv - vec2(uv.x, 0)));
    col += 1.0 - smoothstep(0.2, 0.3, length(uv - vec2(floor(uv.x + 0.5), uv.y)));


    col *= 0.5 + 0.5 * cos(6.28 * vec3(0.1, 0.33, 0.66) + uv.y * 1.5 + iTime * 1.2 + col * 2.0);


    col *= 0.8 + 0.2 * sin(iTime * 0.5 + uv.x * 3.0);


    fragColor = vec4(col, 1.0);
}
