#define time iTime
#define clock time / 5.0
#define ttime floor(time) + pow(fract(time), sin(iTime * 1.333) * 0.5)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    uv.y += tan(abs(uv.y) * 5.0);


    float d = fract(10.0 * uv.y + clock);
    d = smoothstep(0.2, 0.10, d);

    float p = tan(uv.y + time);
    vec3 col = mix(
        vec3(1.0),
        vec3(
            p * 0.1 + step(0.1, abs(cos(clock * 2.0)) * uv.y),
            p * 0.9,
            0.1 + step(1.0, sin(clock) * sin(clock) * abs(uv.y))
        ),
        d
    );


    fragColor = vec4(col, d * sin(ttime + 1.0 - floor(length(floor(uv * 10.0)))));
}
