#define PI 3.1415926
#define e  2.718281
vec2 translateUV(vec2 uv)
{
    float r = length(uv)*sqrt(2.0);
    float eta = atan(uv.y,uv.x);
    return vec2(eta,r);
}
vec2 inversetranslateUV(vec2 uv){
    return vec2(uv.r*cos(uv.x),uv.r*sin(uv.y));
}
