        vec2 u = U/R;
        Q += sin(-2.+(50.-i)/50.*3.1+vec4(1,2,3,4))*exp(-(u.y-q.x)*(u.y-q.x)/(1e-5+dFdx(q.x)*dFdx(q.x)));
    }



}
