#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1)*e;
    p.z-=6.;
    p.zy*=R2D(iTime*.3);

    for(j=0.;j++<8.;)
        p.xy=abs(abs((p.xy-1.4)*R2D(iTime*.2))),
        d=max(max(p.z,p.y),p.x)-.5,
        d-=dot(p,q)/2e4;

    e+=d;

}o+=vec4(exp(-p-q),1)*3./i;
}
