#define res iResolution.xy
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * res) / res.y;
    vec2 ouv = uv;

    vec3 col = vec3(0.0);
    uv = fract(uv*30.0);
    uv -= 0.5;
    uv *= 2.7;
    uv = vec3(length(uv)).rr;

    col = 1.0-uv.xyy;
    col *= vec3(length(ouv));

    fragColor = vec4(col,1.0);
}
