void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0; 
    uv.y += 0.15;

    float wave = sin(uv.x + iTime); 
    wave *= wave * 50.0;

    vec3 waveColor = vec3(0.0);

    for (float i = 0.0; i < 10.0; i++) {
        float luma = abs(1.0 / (100.0 * uv.y + wave));
        float y = sin(uv.x * sin(iTime) + i * 0.2 + iTime);
        uv.y += 0.05 * y;

        vec3 rainbow = vec3(
            sin(i * 0.3 + iTime) * 0.5 + 0.5,
            sin(i * 0.3 + 2.0 + sin(iTime * 0.3) * 2.0) * 0.5 + 0.5,
            sin(i * 0.3 + 4.0) * 0.5 + 0.5
        );

        waveColor += rainbow * luma;
    }

    fragColor = vec4(waveColor, 1.0);
}
