void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize coordinates to the range [0, 1]
    vec2 uv = fragCoord / iResolution.xy;

    // Center the UV vertically and scale it for better aspect ratio handling
    uv.y *= iResolution.y / iResolution.x;

    // Define the sinusoidal wave with a vertical offset
    float wave = 0.3 + 0.15 * sin(uv.x * 10.0 + iTime * 2.0); // Adjusted amplitude and offset

    // Determine if the current pixel is near the wave
    float line = smoothstep(0.01, 0.0, abs(uv.y - wave)); // Adjust line thickness

    // Output color: white background and black line
    fragColor = vec4(vec3(1.0 - line), 1.0);
}

